/* ======= HEAD BOX QUIZ ======= */
.head_box_quiz{
  margin-top: 1rem;
}
.head_box_quiz .title{
  color: var(--primary-color);
  text-align: center;
}
.head_box_quiz .gameplay{
  color: var(--main-ft-color);
  opacity: .6;
  text-align: center;
  margin: .2rem auto;
}
.head_box_quiz .instruct{
  color: var(--primary-color);
  text-align: center;
  font-size: 1rem;
  margin-top: .5rem;
}

/* ======= CONTENT BOX QUIZ ======= */
.content_box_quiz{
  //background-color: var(--secondary-color);
  padding: 0 1rem;
  display: grid;
  grid-template-areas:
    'assets intitules'
    'assets reponses';
  grid-column-gap: 1rem;
  justify-content: center;
  align-items: stretch;
}

/* ==== INTITULES ==== */
.intitules{
  grid-area: intitules;
  display: flex;
  flex-flow: column nowrap;
  justify-content: flex-end;
  align-items: center;
  font-weight: bold;
  font-size: 1rem;
}
.intitules p{
  padding: 0 1rem;
}

/* ==== ASSETS ==== */
.assets{
  grid-area: assets;
  grid-row: 1 / 3;
  padding: 0 2rem;
  display: flex;
  justify-content: center;
  align-items: center;
}
.assets img, .assets video{
  display: block;
  height: 100%;
  max-height: 500px;
  margin: auto;
}
.assets video{
  cursor: pointer;
}

/* ==== REPONSES ==== */
.reponses{
  grid-area: reponses;
  display: flex;
  flex-flow: column nowrap;
  justify-content: flex-start;
  align-items: stretch;
  margin-bottom: 1rem;
}

/* ==== REPONSES D&D==== */
.reponses.dragDrop_rep{
  align-items: center;
}
.reponses.dragDrop_rep input.droparea{
  padding: .5em;
  border-radius: .3em;
  cursor: default;
  caret-color: white;
}
.reponses.dragDrop_rep div#draggable_box{
  margin-top: 2rem;
  display: flex;
  flex-flow: row;
  justify-content: space-around;
  align-items: center;
  align-content: center;
}
.reponses.dragDrop_rep span.draggable_span{
  //display: inline-block;
  border: 1px solid gray;
  padding: .5rem;
  margin: .8em;
  border-radius: .4em;
  cursor: grab;
}

/* ======= VALID ======= */
input[name="valid"]{
  font-family: "Asap_bold";
  //font-weight: bold;
  text-transform: uppercase;
  letter-spacing: .1rem;
  padding: .75rem 2.5rem;
  margin: 0 auto;
  border-radius: 1.25rem/50%;
  font-size: 1rem;
  background-color: var(--primary-color);
  color: var(--main-bg-color);
  cursor: pointer;
  border: 1px solid var(--main-bg-color);
}
input[name="valid"]:hover{
  background-color: var(--secondary-color);
  color: var(--primary-color);
  border: 1px solid var(--primary-color);
}
